<style lang="scss">
@import '@/assets/scss/global.scss';

.hhb-introduction {
   // width: 100%;
   padding: 30px;
   display: flex;
   flex-direction: column;
   align-items: center;

   @include mq (lg) {
      padding: 20px 30px;
      right: 0;    
   }
}

.introduction-header-section-wrapper {
   width: 100%;
   display: flex;
   flex-direction: row;
   justify-content: space-between;
   align-items: center;
}

.header-section-logo {
   flex-basis: 20%;
   img {
      width: 100%;
   }
}

.header-section-controllers {
   flex-basis: 20%;

   .header-burger-menu {
      display: flex;
      align-items: center;
      justify-content: left;
      margin-left: 10px;

      @include mq (lg) {
         display: none;
      }
   }

   .header-language-toggle {
      display: none;
      
      @include mq (lg) {
         display: flex;
      }

      input[type=checkbox]{
         height: 0;
         width: 0;
         visibility: hidden;
      }

      label {
         cursor: pointer;
         text-indent: -9999px;
         width: 70px;
         height: 40px;
         background: grey;
         display: block;
         border-radius: 50px;
         position: relative;
      }

      label:after {
         content: '';
         position: absolute;
         top: 5px;
         left: 5px;
         width: 30px;
         height: 30px;
         background: #fff;
         border-radius: 90px;
         transition: 0.3s;
      }

      input:checked + label {
         background: $primary;
      }

      input:checked + label:after {
         left: calc(100% - 5px);
         transform: translateX(-100%);
      }

      label:active:after {
         width: 70px;
      }
   }   
}

.introduction-title {
   margin: 30px;
   padding: 10px 60px;
   width: 100%;
   border-radius: 50px;
   background-color: $primary;
   text-align: center;
   
   @include mq (lg) {
      padding: 10px 10px;
      margin: 50px 0px 20px;
   }

   @include mq (xxl) {
      margin-top: 80px;
   }

   h1{
      color: #fff !important; 
      font-size: 24px;

      @include mq (sm) {
         font-size: 28px;
      }

      @include mq (md) {
         font-size: 32px;
      }

      @include mq (lg) {
         font-size: 28px;
      }
   }
}

.introduction-navbar {
   display: none;
   width: 100%;

   @include mq (lg) {
      margin-bottom: 40px;
      display: initial;
   }

   @include mq (xl) {
      margin-top: 50px;
      margin-bottom: 50px;
   }

   @include mq (xxl) {
      margin: 70px 0px;
   }

   ul {
      list-style-type: none;
      width: 100%;
      display: flex;
      flex-direction: row;
      justify-content: space-between;
      align-items: center;
      flex-wrap: wrap;
      li {
         flex-basis: 50%; 
         margin-top: 30px;     
         
         @include mq (xl) {
         flex-basis: 25%; 
         margin-top: 0px;     
         
         }
      }

      .nav-link {
         display: flex;
         flex-direction: column;
         align-items: center;
         
         path {
            fill: #000;
         }

         p {
            margin-top: 10px;
         }

         &:hover {
            path {
               fill: $secondary
            }
            p {
               color: $secondary;
            }
         }
      }
   }

}

.introduction-description {
   p {
      text-align: center;
      color: $primary;
   }
}

.introduction-social-media {
   margin-top: 30px;
   width: 55px;
   height: 55px;
   border-radius: 50%;
   background-color: $primary;
   display: flex;
   align-items: center;
   justify-content: center;

   @include mq (md) {
      margin-top: 20px;
   }

   @include mq (xl) {
      margin-top: 60px;
   }

   @include mq (xxl) {
      margin-top: 80px;
   }
}
</style>
